import {csrfFetch} from './csrf'

const SET_USER = 'session/setUser'
const REMOVE_USER = 'session/removeUser'

const actionSetUser = (user) => {
    return {
        type: SET_USER,
        payload: user,
    }
}

const actionRemoveUser = () => {
    return {
        type: REMOVE_USER,
    }
}

export const thunkLogin = (user) => async dispatch => {
    const {credential, password} = user
    const res = await csrfFetch('/api/session', {
        method: 'POST',
        body: JSON.stringify({
            credential,
            password,
        }),
    })
    const data = await res.json()
    dispatch(actionSetUser(data.user))
    return res
}

export const restoreUser = () => async dispatch => {
    const response = await csrfFetch('/api/session')
    const data = await response.json()
    dispatch(setUser(data.user))
    return response
}

export const signupUser = (user) => async dispatch => {
    const {email, username, firstName, lastName, password} = user
    const response = await csrfFetch('/api/users', {
        method: 'POST',
        body: JSON.stringify({
            username,
            firstName,
            lastName,
            email,
            password,
        }),
    })
    const newUser = await response.json()
    dispatch(setUser(newUser.user))
    return response
}

export const logoutUser = () => async dispatch => {
    const response = await csrfFetch('/api/session' , {
        method: 'DELETE',
    })
    dispatch(removeUser())
    return response
}

const initialState = {user: null}

const sessionReducer = (state = initialState, action) => {
    let newState
    switch (action.type) {
        case SET_USER:
            newState = Object.assign({}, state)
            newState.user = action.payload
            return newState
        case REMOVE_USER:
            newState = Object.assign({}, state)
            newState.user = null
            return newState
        default: 
            return state;
    }
}

export default sessionReducer
