import React, { useState, useEffect } from 'react';
import Container from 'react-bootstrap/esm/Container';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

import { csrfFetch } from '../../store/csrf';

const apiKey = "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"

function ChatWidget() {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setMessages((prevMessages) => [...prevMessages, inputValue]);
    const data = await sendMessage(inputValue)
    setInputValue('');
  };

  const handleRemoveChat = () => {
    setMessages([])
  }

  const sendMessage = async (message) => {
    try {
      const response = await csrfFetch('/api/chats/gpt', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({message})
      })
    } catch (error) {
      console.error(error)
    }
  }

  // const sendMessageToBot = async (inputValue) => {
  //   const response = await sendMessage({
  //     model: 'davinci',
  //     prompt: inputValue,
  //     temperature: 0.5,
  //     maxTokens: 50,
  //     n: 1,
  //     stop: ['\n']
  //   });

  //   const message = response.choices[0].text.trim();
  //   setMessages((prevMessages) => [...prevMessages, message]);
  // }

  // useEffect(() => {
  //   sendMessageToBot("Hello")
  // }, [])

  return (
    <Container>

      <div>
        {messages.map((message, index) => (
          <Card style={{ width: '18rem' }} key={index}>{message}</Card>
        ))}
      </div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={inputValue}
          onChange={handleInputChange}
          placeholder="Type your message here"
        />
        <Button type="submit">Send</Button>
      </form>
      <Button onClick={handleRemoveChat}>Remove Chat History</Button>
    </Container>
  );
}

export default ChatWidget;
